import './App.css';
import React, {useEffect, useState} from "react";
import Recipe from './components/Recipe.js';
import axios from 'axios';

const App = () => {

  const APP_ID = "6715b9c6245c426eb8168e607b317045";


  const [recipes, setRecipes] = useState([]);
  const [search, setSearch] = useState('');
  const [query, setQuery] = useState('');
  const [id, setId] = useState([]);
  const [instru, setInstru] = useState([]);

  useEffect(() => {
     getRecipes();  
    }, [query]
  );

  const getRecipes = async () => {
    const response = await fetch(
      `https://api.spoonacular.com/recipes/complexSearch?query=${query}&apiKey=${APP_ID}`
    );
    const data = await response.json();
    setRecipes(data.results);
    recipes.map(recipe => (
          setId(recipe.id)
      ))

  const instruction = await axios.get(
        `https://api.spoonacular.com/recipes/${id}/information?includeNutrition=false&apiKey=${process.env.REACT_APP_API_KEY}`
    );
    setInstru(instruction.data);
  }; 


  const updateSearch = e => {
    setSearch(e.target.value);
  };

  const getSearch = e => {
    e.preventDefault();
    setQuery(search);
    setSearch('');
  }

  return(
    <div className="App">
        <form className="search-form" onSubmit={getSearch}>
          <input className="search-bar" type="text" value={search} onChange={updateSearch}/>
          <button  className="search-button" type="submit"> 
            Search
          </button>
        </form>
        {recipes.map(recipe => (
          <Recipe
            key = {recipe.id}
            title = {recipe.title}
            image = {recipe.image}
          />
        ))}
    </div>
  );
}



export default App;
